def divide(num, den):
    if den > 0.0:
        return num / den
    else:
        return 0

def undummy(df):
    if df['main_category_Art'] == 1:
        return 'Art'
    else:
        if df['main_category_Comics']  == 1:
            return 'Comics'
        else:
            return 'No category'

def gap_estimator(category, backers, goal_real):
    if category == "Design":
        factor = 7182
    else: 
        factor = 9961
    #factor = 100    
    gap_estimated = factor + 75.2004 * backers - 0.9998 * goal_real 
    return gap_estimated
